import React from 'react'
import { useLocation } from 'react-router-dom';
import { BsArrowLeft } from 'react-icons/bs';


function Details() {
    const location = useLocation();
    const { country } = location.state;

    return (
        <div className="details">
            <button className="back flex flex-ai-c"><BsArrowLeft/>Back</button>
            <div className="details__container flex flex-jc-sb flex-ai-c">
                <img src={country.flag} alt={country.name}/>
                <div className="details__body">
                    <div>
                    <h3 className="card__name">{country.name}</h3>
                    <p>
                        <strong>Population: </strong>{country.population}
                    </p>
                    <p>          
                        <strong>Region: </strong>{country.region}
                    </p>
                    <p>          
                        <strong>Capital: </strong>{country.capital}
                    </p>
                    <p>
                        <strong>Native name: </strong>{country.nativeName}
                    </p>
                    <p>
                        <strong>Population: </strong>{country.population}
                    </p>
                    <p>
                        <strong>Region: </strong>{country.region}
                    </p>
                    <p>
                        <strong>Sub region: </strong>{country.subregion}
                    </p>
                    <p>
                        <strong>Capital: </strong>{country.capital}
                    </p>
                    </div>

                    <div>
                        <p>
                            <strong>Top Level Domain: </strong>{country.topLevelDomain[0]}
                        </p>
                        <p>
                            <strong>Currencies: </strong>{country.currencies.map(currency => currency.code + ', ')}
                        </p>
                        <p>
                            <strong>Languages: </strong>{country.languages.map(language => language.name + ', ')}
                        </p>
                    </div>


                    {/* <div>
                        <p>Border Countries</p>
                        <button></button>
                        <button></button>
                        <button></button>
                    </div> */}
                </div>
            </div>

        </div>
    )
}

export default Details
